import React, { Component } from 'react';
import { Grid, Cell } from 'react-mdl';
import { withRouter } from 'react-router-dom';
import { withAuthenticator } from 'aws-amplify-react';
import './ProfilePage.css';
import { Auth } from 'aws-amplify';

class ProfilePage extends Component {
  state={
    oldpassword: "",
    newpassword: "",
    confirmpassword: "",
    errors: {
      cognito: null,
      blankfield: false,
      passwordmatch: false
    }
  }
  constructor(){
    super();
    this.state=({
      name: ''
    })
    this.handleBackClick = this.handleBackClick.bind(this);
  }

  handleBackClick(e) {
    e.preventDefault();
    this.props.history.push('/room-list');
  }

  getUserInfo() {
    Auth.currentUserInfo().then((userInfo) => {
      const { username } = userInfo;
      this.setState({
        name: username
      });
    });
  }

  //get score info from db
  getScore() {

  }

  componentDidMount() {
    this.getUserInfo();
  }

  FormValidate(event, state) {
    // clear all error messages
    const inputs = document.getElementsByClassName("is-danger");
    for (let i = 0; i < inputs.length; i++) {
      if (!inputs[i].classList.contains("error")) {
        inputs[i].classList.remove("is-danger");
      }
    }

    if (state.hasOwnProperty("username") && state.username === "") {
      document.getElementById("username").classList.add("is-danger");
      return { blankfield: true };
    }
    if (state.hasOwnProperty("firstname") && state.firstname === "") {
      document.getElementById("firstname").classList.add("is-danger");
      return { blankfield: true };
    }
    if (state.hasOwnProperty("lastname") && state.lastname === "") {
      document.getElementById("lastname").classList.add("is-danger");
      return { blankfield: true };
    }
    if (state.hasOwnProperty("email") && state.email === "") {
      document.getElementById("email").classList.add("is-danger");
      return { blankfield: true };
    }
    if (
      state.hasOwnProperty("verificationcode") &&
      state.verificationcode === ""
    ) {
      document.getElementById("verificationcode").classList.add("is-danger");
      return { blankfield: true };
    }
    if (state.hasOwnProperty("password") && state.password === "") {
      document.getElementById("password").classList.add("is-danger");
      return { blankfield: true };
    }
    if (state.hasOwnProperty("oldpassword") && state.oldpassword === "") {
      document.getElementById("oldpassword").classList.add("is-danger");
      return { blankfield: true };
    }
    if (state.hasOwnProperty("newpassword") && state.newpassword === "") {
      document.getElementById("newpassword").classList.add("is-danger");
      return { blankfield: true };
    }
    if (state.hasOwnProperty("confirmpassword") && state.confirmpassword === "") {
      document.getElementById("confirmpassword").classList.add("is-danger");
      return { blankfield: true };
    }
    if (
      state.hasOwnProperty("password") &&
      state.hasOwnProperty("confirmpassword") &&
      state.password !== state.confirmpassword
    ) {
      document.getElementById("password").classList.add("is-danger");
      document.getElementById("confirmpassword").classList.add("is-danger");
      return { passwordmatch: true };
    }
    if (
      state.hasOwnProperty("newpassword") &&
      state.hasOwnProperty("confirmpassword") &&
      state.newpassword !== state.confirmpassword
    ) {
      document.getElementById("newpassword").classList.add("is-danger");
      document.getElementById("confirmpassword").classList.add("is-danger");
      return { passwordmatch: true };
    }
    return;
  }

  handlesubmit = async event =>{
    event.preventDefault();
    this.clearErrorState();
    const error = FormValidate(event, this.state);
    if(error){
      this.setState({
        errors:{...this.state.eeror, ...error}
      });
    }
    // AWS Cognito integration
    try {
      const user = await Auth.currentAuthenticatedUser();
      console.log(user);
      await Auth.changePassword(
        user, 
        this.state.oldpassword,
        this.state.newpassword
      );
      this.props.history.push("")
    }
    catch(error){
      console.log(error)

    }
  }

  render() {
    return (
        <div className="profile">
          <button className="profile-back-button" onClick={this.handleBackClick}>Back</button>
                <img
                    src="https://www.shareicon.net/download/2015/09/18/103157_man_512x512.png"
                    alt="profile-img"
                    className="profile-img"
                />

                <div className="username">
                    <h1>{this.state.name}</h1>

                </div>
                <p className="score">Win: | Lost: | Presentage: |</p>

                <div className="passwordsetting">
                <h1>Change Password</h1>
                <div className="oldpassword">
                <input className="input" type="password" id="oldpassword" placeholder="Old Password"></input>
                </div>
                <div className="newpassword">
                <input className="input" type="password" id="newpassword" placeholder="New Password"></input>
                </div>
                <div className="confirmpassword">
                <input className="input" type="password" id="confirmpassword" placeholder="Confirm Paaword"></input>
                </div>
                <div className="submitbutton">
                <button className="submit" type="submit" id="submit">Submit</button>
                </div>
                </div>

        </div>
    )
}
}

export default withRouter(withAuthenticator(ProfilePage,true));
