import React, { Component } from 'react';
import bg from '../img/background.png';
import './RoomListPage.css';
import Room from './Components/Room';

class RoomListPage extends React.Component {
    constructor(){
        super();
        this.state={
            roomID: [],
            player_count: [],
            status: []
        };
        this.handleClick = this.handleClick.bind(this);
    }

    handleClick(){
        alert("button clicked");
    }

    renderRoom(){
        return(
            <div>
                <button onClick={this.handleClick}>
                    {this.state.roomID}
                    {this.state.player_count}
                    {this.state.status}
                </button>
            </div>
        );
    }

    render() {
        getRoomID();
        getPlayerCount();
        getStatus();

        return (
            <div className="room-list">
                <p className="room-header">SWITCH</p>
                <img src={bg} className="room-bg" alt="background"/>
                {this.renderRoom(0)}
                <button>Prev</button>
                <button>Next</button>
                <form>
                    <label className="room-num">Room #: <input type="number" name="room-num-input" /></label>
                    <input type="submit" value="ENTER" name="enter-button"/>
                </form>
                <button className="button">Create New Room</button>
                <button className="button">Random Match</button>
            </div>
        );
    }
}

function getRoomID(){
    this.setState({
        roomID: [...this.state.roomID, 1]
    });
}

function getPlayerCount(){
    this.setState({
        player_count: [...this.state.player_count, 4]
    });
}

function getStatus(){
    this.setState({
        status: [...this.state.status, 'playing']
    });
}

export default RoomListPage;
