async initCardData(card,x,y,theusername,theSeat){
	const cardV = card;
	console.log(cardV)
	const xV =x;
	console.log("x : "+xV)
	const yV = y;
	console.log("y : "+yV);
	const name = theusername;
	console.log('your name : ' +name);
	const thething = {
				username : name,
				whichCard : cardV,
						x : xV,
						y : yV,
						seat: theSeat
					};
 const newThing = await API.graphql(graphqlOperation(mutations.updateQw, {input: thething}));
}


	
	
	async updateCardData(card,x,y,name,theSeat){
		const cardV = card;
		//console.log(cardV)
		const xV =x;
		//console.log("x : "+xV)
		const yV = y;
		//console.log("y : "+yV);
		//console.log('your name : ' +name);
		const thething = {
					username : name,
					whichCard : cardV,
							x : xV,
							y : yV,
							seat: theSeat
						};
	 const newThing = await API.graphql(graphqlOperation(mutations.updateQw, {input: thething}));
	}


	//click the card and make it move
	clickedBox(ranNums){
		this.input.on('gameobjectdown', (pointer, gameObject) => {
			for(var i=0;i<36;i++){
				if(this.gameBoard[i] == i ){
					if(gameObject.x==this.player[this.round].x||gameObject.y==this.player[this.round].y){
						if(gameObject.data.get('card_number') == i){
							//console.log('another test for seat '+this.seat)
								this.checkUserInfo(i,this.userName[this.round],gameObject.x,gameObject.y)
								this.CardLeft--;
								this.arrange+=20
								break;
						}else if(gameObject.data.get('card_number') == 53){
							this.checkUserInfo(-1,this.userName[this.round],gameObject.x,gameObject.y)
								break;
						}
					}	
			}	
	
	}

			
	});

}

async updateScreen(){
	(async () => { 
 
		await client.hydrated();
						
		var nameWeGot1 = this.userName[0];
		const result1 = await client.query({
			query: gql(queries.getQw),
			variables: {
